import { MdInput, Toolbar } from "@choice-ui/md-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState(
    "### Heading\n\n**Bold text** and *italic text*\n\n- List item 1\n- List item 2",
  )
  return (
    <MdInput
      value={value}
      onChange={setValue}
    >
      <MdInput.Header>
        <MdInput.Tabs />
        <MdInput.Toolbar />
      </MdInput.Header>
      <MdInput.Container>
        <MdInput.Editor placeholder="Enter markdown..." />
        <MdInput.Render />
      </MdInput.Container>
    </MdInput>
  )

}

