import { MdInput, Toolbar } from "@choice-ui/md-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState("Mention team members:\n\n")

  const mentionItems = [
    {
      id: "1",
      label: "John Doe",
      email: "john@example.com",
      role: "Developer",
    },
    {
      id: "2",
      label: "Jane Smith",
      email: "jane@example.com",
      role: "Designer",
    },
    {
      id: "3",
      label: "Bob Johnson",
      email: "bob@example.com",
      role: "Manager",
    },
    {
      id: "4",
      label: "Alice Williams",
      email: "alice@example.com",
      role: "Developer",
    },
  ]

  const handleMentionSelect = (
    item: { email?: string; id: string; label: string; role?: string },
    query: string,
  ) => {
    return `[@${item.label}](mailto:${item.email || ""}) `
  }

  return (
    <MdInput
      value={value}
      onChange={setValue}
      mentionItems={mentionItems}
      mentionOnSelect={handleMentionSelect}
    >
      <MdInput.Header>
        <MdInput.Tabs />
        <MdInput.Toolbar />
      </MdInput.Header>
      <MdInput.Container>
        <MdInput.Editor placeholder="Type @ to mention someone..." />
        <MdInput.Render />
      </MdInput.Container>
    </MdInput>
  )

}

