import { NumericInput } from "@choice-ui/numeric-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState(10)
  const [menuValue, setMenuValue] = useState("fixed")
  const [menuOpen, setMenuOpen] = useState(false)

  return (
    <NumericInput
      className="w-64"
      focused={menuOpen}
      value={value}
      onChange={(newValue) => setValue(newValue as number)}
    >
      {menuValue !== "fixed" && (
        <NumericInput.ActionPrompt>
          {selectOptions.find((option) => option.value === menuValue)?.label?.split(" ")[0]}
        </NumericInput.ActionPrompt>
      )}

      <NumericInput.Suffix type="action">
        <Select
          open={menuOpen}
          onOpenChange={setMenuOpen}
          value={menuValue}
          onChange={(value) => setMenuValue(value)}
          placement="bottom-end"
        >
          <Select.Trigger asChild>
            <IconButton className="rounded-l-none">
              <ChevronDownSmall />
            </IconButton>
          </Select.Trigger>
          <Select.Content>
            <Select.Item value="fixed">
              <FixedHeight />
              Fixed height
            </Select.Item>
            <Select.Item value="hug">
              <HugHeight />
              Hug contents
            </Select.Item>
            <Select.Divider />
            <Select.Item value="min">
              <MinHeight />
              Add min height...
            </Select.Item>
            <Select.Item value="max">
              <MaxHeight />
              Add max height...
            </Select.Item>
          </Select.Content>
        </Select>
      </NumericInput.Suffix>
    </NumericInput>
  )

}

import { NumericInput } from "@choice-ui/numeric-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<number | undefined>(undefined)
  const [variableValue, setVariableValue] = useState<number | null>(null)

  const handleChange = useEventCallback((newValue) => {
    setValue(newValue as number)
  })

  return (
    <NumericInput
      className="w-64"
      value={value}
      onChange={handleChange}
    >
      <NumericInput.Prefix>
        <FixedWidth />
      </NumericInput.Prefix>

      {variableValue && !value && <NumericInput.Variable value={variableValue} />}

      <NumericInput.Suffix type="menu">
        <Dropdown>
          <Dropdown.Trigger asChild>
            <IconButton className="rounded-l-none">
              <Variable />
            </IconButton>
          </Dropdown.Trigger>
          <Dropdown.Content>
            <Dropdown.Item
              onMouseUp={() => {
                setVariableValue(10)
                setValue(undefined)
              }}
            >
              Add variable...
            </Dropdown.Item>
          </Dropdown.Content>
        </Dropdown>
      </NumericInput.Suffix>
    </NumericInput>
  )

}

