import { NumericInput } from "@choice-ui/numeric-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<number | undefined>(100)

  return (
    <div className="flex flex-col gap-4">
      <div className="flex items-center gap-2">
        <NumericInput
          value={value}
          onChange={(newValue) => {
            if (typeof newValue === "number" || newValue === undefined) {
              setValue(newValue)
            }
          }}
        >
          <NumericInput.Prefix>
            <HugWidth />
          </NumericInput.Prefix>
        </NumericInput>

        <Button
          variant="secondary"
          onClick={() => setValue(undefined)}
        >
          Clear
        </Button>
      </div>

      <div className="bg-secondary-background flex flex-col gap-2 rounded-lg p-4">
        <p className="text-secondary-foreground">
          <b>Current value:</b> {value === undefined ? "empty" : value}
        </p>
      </div>
    </div>
  )

}

import { NumericInput } from "@choice-ui/numeric-input"
import { useState } from "react"

export default function App() {

  const [color, setColor] = useState({ r: 255, g: 100, b: 50 })

  const rgbToHex = (r: number, g: number, b: number) => {
    return `#${r.toString(16).padStart(2, "0")}${g.toString(16).padStart(2, "0")}${b.toString(16).padStart(2, "0")}`
  }

  const hexColor = rgbToHex(color.r, color.g, color.b)

  return (
    <div className="flex flex-col gap-4">
      <div className="grid max-w-md grid-cols-3 gap-4">
        <div className="flex flex-col gap-2">
          <Label className="text-red-600">Red</Label>
          <NumericInput
            value={color.r}
            min={0}
            max={255}
            step={1}
            onChange={(value) => setColor((prev) => ({ ...prev, r: value as number }))}
          >
            <NumericInput.Prefix>
              <ColorTypeSolid />
            </NumericInput.Prefix>
          </NumericInput>
        </div>

        <div className="flex flex-col gap-2">
          <Label className="text-green-600">Green</Label>
          <NumericInput
            value={color.g}
            min={0}
            max={255}
            step={1}
            onChange={(value) => setColor((prev) => ({ ...prev, g: value as number }))}
          >
            <NumericInput.Prefix>
              <ColorTypeSolid />
            </NumericInput.Prefix>
          </NumericInput>
        </div>

        <div className="flex flex-col gap-2">
          <Label className="text-blue-600">Blue</Label>
          <NumericInput
            value={color.b}
            min={0}
            max={255}
            step={1}
            onChange={(value) => setColor((prev) => ({ ...prev, b: value as number }))}
          >
            <NumericInput.Prefix>
              <ColorTypeSolid />
            </NumericInput.Prefix>
          </NumericInput>
        </div>
      </div>

      <div className="flex items-center gap-4">
        <div
          style={{
            backgroundColor: hexColor,
            width: "100px",
            height: "50px",
            borderRadius: "6px",
          }}
        />
        <div className="flex flex-col">
          <div className="text-body-large font-mono">{hexColor}</div>
          <div className="text-secondary-foreground">
            rgb({color.r}, {color.g}, {color.b})
          </div>
        </div>
      </div>
    </div>
  )

}
