import { Component } from "@choice-ui/numeric-input"

export default function App() {
  return (
    <NumericInput value={value} onChange={handleChange}>
      <NumericInput.Prefix>
        <FixedWidth />
      </NumericInput.Prefix>

      {variableValue && !value && (
        <NumericInput.Variable value={variableValue} />
      )}

      <NumericInput.Suffix type="menu">
        <Dropdown>
          <Dropdown.Trigger asChild>
            <IconButton className="rounded-l-none">
              <Variable />
            </IconButton>
          </Dropdown.Trigger>
          <Dropdown.Content>
            <Dropdown.Item
              onMouseUp={() => {
                setVariableValue(10);
                setValue(undefined);
              }}
            >
              Add variable...
            </Dropdown.Item>
          </Dropdown.Content>
        </Dropdown>
      </NumericInput.Suffix>
    </NumericInput>
  );
}

import { Component } from "@choice-ui/numeric-input"

export default function App() {
  return (
    <NumericInput
      expression="{width}px, {height}px"
      value={value}
      onChange={(newValue) =>
        setValue(newValue as { height: number; width: number })
      }
    >
      <NumericInput.Prefix>
        <FixedWidth />
      </NumericInput.Prefix>
    </NumericInput>
  );
}

