import { OtpInput } from "@choice-ui/otp-input"

export default function App() {
  return (

    <OtpInput maxLength={4}>
      <OtpInput.Group>
        <OtpInput.Slot index={0} />
        <OtpInput.Slot index={1} />
        <OtpInput.Slot index={2} />
        <OtpInput.Slot index={3} />
      </OtpInput.Group>
    </OtpInput>

  );
}

import { OtpInput } from "@choice-ui/otp-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState("")

  return (
    <div className="space-y-4">
      <OtpInput
        maxLength={6}
        value={value}
        onChange={setValue}
        onComplete={(code) => alert(`Code submitted: ${code}`)}
      >
        <OtpInput.Group>
          <OtpInput.Slot index={0} />
          <OtpInput.Slot index={1} />
          <OtpInput.Slot index={2} />
        </OtpInput.Group>
        <OtpInput.Separator />
        <OtpInput.Group>
          <OtpInput.Slot index={3} />
          <OtpInput.Slot index={4} />
          <OtpInput.Slot index={5} />
        </OtpInput.Group>
      </OtpInput>
      <p className="text-secondary-foreground text-sm">Current value: {value || "(empty)"}</p>
    </div>
  )

}
