import { Radio } from "@choice-ui/radio"

export default function App() {

  enum State {
    Disabled = "disabled",
    Focused = "focused",
    Rest = "rest",
  }

  enum Interaction {
    Off = "off",
    On = "on",
  }

  enum Variant {
    Accent = "accent",
    Default = "default",
    Outline = "outline",
  }

  return (
    <div className="flex flex-col items-start gap-4">
      {Object.values(Variant).map((variant, index) => (
        <Fragment key={variant}>
          <span className="capitalize">{variant}</span>
          <div className="grid grid-cols-3 gap-2">
            {Object.values(State).map((state) => (
              <Fragment key={state}>
                <span className="capitalize text-pink-500">{state}</span>

                {Object.values(Interaction).map((interaction) => (
                  <Fragment key={interaction}>
                    <Radio
                      value={interaction === Interaction.On}
                      disabled={state === State.Disabled}
                      focused={state === State.Focused}
                      variant={variant}
                      onChange={(value) => {
                        console.log(value)
                      }}
                    >
                      {interaction}
                    </Radio>
                  </Fragment>
                ))}
              </Fragment>
            ))}
          </div>
          {index !== Object.values(Variant).length - 1 && <hr className="w-full" />}
        </Fragment>
      ))}
    </div>
  )

}

import { Radio } from "@choice-ui/radio"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState(false)
  return (
    <Radio
      value={value}
      onChange={setValue}
      disabled
    >
      Disabled
    </Radio>
  )

}

import { Radio } from "@choice-ui/radio"
import { useState } from "react"

export default function App() {

  const [variant, setVariant] = useState({
    default: false,
    accent: false,
    outline: false,
  })

  return (
    <>
      <Radio
        name="variant"
        value={variant.default}
        onChange={(value) => setVariant({ ...variant, default: value })}
      >
        Default
      </Radio>
      <Radio
        name="variant"
        value={variant.accent}
        onChange={(value) => setVariant({ ...variant, accent: value })}
        variant="accent"
      >
        Accent
      </Radio>
      <Radio
        name="variant"
        value={variant.outline}
        onChange={(value) => setVariant({ ...variant, outline: value })}
        variant="outline"
      >
        Outline
      </Radio>
    </>
  )

}

import { Radio } from "@choice-ui/radio"
import { useState } from "react"

export default function App() {

  const [simple, setSimple] = useState(false)
  const [explicit, setExplicit] = useState(false)

  return (
    <div className="flex flex-col gap-4">
      <div>
        <h4 className="font-strong mb-2">Simple string label (auto-wrapped):</h4>
        <Radio
          value={simple}
          onChange={setSimple}
        >
          Simple text label
        </Radio>
      </div>

      <div>
        <h4 className="font-strong mb-2">Explicit Radio.Label (for complex content):</h4>
        <Radio
          value={explicit}
          onChange={setExplicit}
        >
          <Radio.Label>
            <span className="text-accent-foreground">Complex</span> label with{" "}
            <strong>formatting</strong>
          </Radio.Label>
        </Radio>
      </div>
    </div>
  )

}

import { RadioGroup } from "@choice-ui/radio"
import { useState } from "react"

export default function App() {

  const groupOptions = [
    { value: "option1", label: "Option 1" },
    { value: "option2", label: "Option 2" },
    { value: "option3", label: "Option 3" },
  ]

  const [optionsValue, setOptionsValue] = useState("option1")
  const [itemsValue, setItemsValue] = useState("option1")

  return (
    <div className="grid grid-cols-2 gap-8">
      <div className="space-y-2">
        <p className="text-secondary-foreground">Using options prop</p>
        <RadioGroup
          options={groupOptions}
          value={optionsValue}
          onChange={setOptionsValue}
        />
      </div>

      <div className="space-y-2">
        <p className="text-secondary-foreground">Using RadioGroup.Item</p>
        <RadioGroup
          value={itemsValue}
          onChange={setItemsValue}
        >
          {groupOptions.map((option) => (
            <RadioGroup.Item
              key={option.value}
              value={option.value}
            >
              {option.label}
            </RadioGroup.Item>
          ))}
        </RadioGroup>
      </div>
    </div>
  )

}

import { RadioGroup } from "@choice-ui/radio"
import { useState } from "react"

export default function App() {

  const [defaultValue, setDefaultValue] = useState("option1")
  const [accentValue, setAccentValue] = useState("option1")
  const [outlineValue, setOutlineValue] = useState("option1")

  const options = [
    { value: "option1", label: "Option 1" },
    { value: "option2", label: "Option 2" },
    { value: "option3", label: "Option 3" },
  ]

  return (
    <div className="grid grid-cols-3 gap-8">
      <div className="space-y-2">
        <p className="text-secondary-foreground">Default</p>
        <RadioGroup
          variant="default"
          value={defaultValue}
          onChange={setDefaultValue}
        >
          {options.map((option) => (
            <RadioGroup.Item
              key={option.value}
              value={option.value}
            >
              {option.label}
            </RadioGroup.Item>
          ))}
        </RadioGroup>
      </div>

      <div className="space-y-2">
        <p className="text-secondary-foreground">Accent</p>
        <RadioGroup
          variant="accent"
          value={accentValue}
          onChange={setAccentValue}
        >
          {options.map((option) => (
            <RadioGroup.Item
              key={option.value}
              value={option.value}
            >
              {option.label}
            </RadioGroup.Item>
          ))}
        </RadioGroup>
      </div>

      <div className="space-y-2">
        <p className="text-secondary-foreground">Outline</p>
        <RadioGroup
          variant="outline"
          value={outlineValue}
          onChange={setOutlineValue}
        >
          {options.map((option) => (
            <RadioGroup.Item
              key={option.value}
              value={option.value}
            >
              {option.label}
            </RadioGroup.Item>
          ))}
        </RadioGroup>
      </div>
    </div>
  )

}
