import { Radio } from "@choice-ui/radio"

export default function App() {
  return (
    <div className="flex flex-col items-start gap-4">
      {Object.values(Variant).map((variant, index) => (
        <Fragment key={variant}>
          <span className="capitalize">{variant}</span>
          <div className="grid grid-cols-3 gap-2">
            {Object.values(State).map((state) => (
              <Fragment key={state}>
                <span className="text-pink-500 capitalize">{state}</span>

                {Object.values(Interaction).map((interaction) => (
                  <Fragment key={interaction}>
                    <Radio
                      value={interaction === Interaction.On}
                      disabled={state === State.Disabled}
                      focused={state === State.Focused}
                      variant={variant}
                      onChange={(value) => {
                        console.log(value);
                      }}
                    >
                      {interaction}
                    </Radio>
                  </Fragment>
                ))}
              </Fragment>
            ))}
          </div>
          {index !== Object.values(Variant).length - 1 && (
            <hr className="w-full" />
          )}
        </Fragment>
      ))}
    </div>
  );
}

import { Radio } from "@choice-ui/radio"

export default function App() {
  return (
    <Radio value={value} onChange={setValue} disabled>
      Disabled
    </Radio>
  );
}

import { Radio } from "@choice-ui/radio"

export default function App() {
  return (
    <>
      <Radio
        name="variant"
        value={variant.default}
        onChange={(value) => setVariant({ ...variant, default: value })}
      >
        Default
      </Radio>
      <Radio
        name="variant"
        value={variant.accent}
        onChange={(value) => setVariant({ ...variant, accent: value })}
        variant="accent"
      >
        Accent
      </Radio>
      <Radio
        name="variant"
        value={variant.outline}
        onChange={(value) => setVariant({ ...variant, outline: value })}
        variant="outline"
      >
        Outline
      </Radio>
    </>
  );
}

import { RadioGroup } from "@choice-ui/radio"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <div className="flex flex-col gap-1">
        <span>Using options prop:</span>
        <RadioGroup
          options={groupOptions}
          value={selectedIds}
          onChange={(value) => setSelectedIds(value)}
        />
      </div>

      <div className="flex flex-col gap-1">
        <span>Using RadioGroup.Item:</span>
        <RadioGroup
          value={selectedIds}
          onChange={(value) => setSelectedIds(value)}
        >
          {groupOptions.map((option) => (
            <RadioGroup.Item key={option.value} value={option.value}>
              {option.label}
            </RadioGroup.Item>
          ))}
        </RadioGroup>
      </div>
    </div>
  );
}

import { RadioGroup } from "@choice-ui/radio"

export default function App() {
  return (
    <>
      <RadioGroup
        variant={variant as "default" | "accent" | "outline"}
        value={variant}
        onChange={(value) => setVariant(value)}
      >
        {groupOptions.map((option) => (
          <RadioGroup.Item key={option.value} value={option.value}>
            {option.label}
          </RadioGroup.Item>
        ))}
      </RadioGroup>
    </>
  );
}
