import { Range } from "@choice-ui/range"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState(0)

  return (
    <div className="grid w-40 grid-cols-[1fr_2.5rem] gap-px">
      <div className="bg-secondary-background flex items-center rounded-l-md px-2">
        <Range
          className="bg-default-boundary flex-1"
          value={value}
          onChange={setValue}
          min={0}
          max={100}
          trackSize={{
            width: "auto",
            height: 6,
          }}
          thumbSize={10}
        />
      </div>
      <NumericInput
        className="before:rounded-l-none"
        expression="{value}%"
        value={value}
        onChange={(value) => setValue(value as number)}
        min={0}
        max={100}
      >
        <NumericInput.Prefix className="w-2 rounded-l-none" />
      </NumericInput>
    </div>
  )

}

import { RangeTuple } from "@choice-ui/range"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<[number, number]>([25, 75])

  return (
    <div className="flex items-center gap-4">
      <RangeTuple
        value={value}
        onChange={setValue}
      />
      <div className="text-body-medium w-20 text-right">
        {value[0]} - {value[1]}
      </div>
    </div>
  )

}

