import { Range, RangeTuple } from "@choice-ui/range"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<[number, number]>([25, 75])

  return (
    <Popover draggable>
      <Popover.Trigger>
        <Button>Open Range Filter</Button>
      </Popover.Trigger>
      <Popover.Header title="Select Range" />
      <Popover.Content className="grid grid-cols-[180px_auto] gap-2 p-3">
        <RangeTuple
          className="flex-1"
          value={value}
          onChange={setValue}
          min={0}
          max={100}
          defaultValue={[25, 75]}
          trackSize={{
            width: 180,
            height: 16,
          }}
        />
        <div className="text-body-medium w-14 flex-1 text-right">
          {value[0]}-{value[1]}%
        </div>
      </Popover.Content>
    </Popover>
  )

}

import { RangeTuple } from "@choice-ui/range"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState<[number, number]>([-0.2, 0.2])

  return (
    <div className="flex items-center gap-4">
      <RangeTuple
        value={value}
        step={0.0001}
        onChange={(value) => {
          console.log("value", value)
          setValue(value)
        }}
        min={-1}
        max={1}
        defaultValue={[-0.25, 0.25]}
      />
      <div className="text-body-medium w-28 text-right">
        {value[0].toFixed(3)} - {value[1].toFixed(3)}
      </div>
    </div>
  )

}
