import { Range } from "@choice-ui/range"

export default function App() {
  return (
    <Popover draggable>
      <Popover.Trigger>
        <Button>Open</Button>
      </Popover.Trigger>
      <Popover.Header title="Range" />
      <Popover.Content className="grid w-64 grid-cols-[180px_auto] gap-2 p-3">
        <Range
          className="flex-1"
          value={value}
          onChange={setValue}
          min={0}
          max={100}
          defaultValue={50}
          trackSize={{
            width: 180,
            height: 16,
          }}
        />
        <div className="w-10 flex-1 text-right">{value}%</div>
      </Popover.Content>
    </Popover>
  );
}

import { Range } from "@choice-ui/range"

export default function App() {
  return (
    <div className="grid w-40 grid-cols-[1fr_2.5rem] gap-px">
      <div className="bg-secondary-background flex items-center rounded-l-md px-2">
        <Range
          className="bg-default-boundary flex-1"
          value={value}
          onChange={setValue}
          min={0}
          max={100}
          trackSize={{
            width: "auto",
            height: 6,
          }}
          thumbSize={10}
        />
      </div>
      <NumericInput
        className="before:rounded-l-none"
        expression="{value}%"
        value={value}
        onChange={(value) => setValue(value as number)}
        min={0}
        max={100}
      >
        <NumericInput.Prefix className="w-2 rounded-l-none" />
      </NumericInput>
    </div>
  );
}

