import { SearchInput } from "@choice-ui/search-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState("Search text without clear button")

  return (
    <SearchInput
      className="w-64"
      value={value}
      onChange={setValue}
      clearable={false}
    />
  )

}

import { SearchInput } from "@choice-ui/search-input"
import { useState } from "react"

export default function App() {

  const [value, setValue] = useState("")
  const [disabled, setDisabled] = useState(false)

  return (
    <div className="flex flex-col gap-2">
      <Checkbox
        value={disabled}
        onChange={(value) => setDisabled(value)}
      >
        Disabled
      </Checkbox>
      <div className="flex flex-wrap gap-4">
        <div className="bg-default-background rounded-lg border p-4">
          <SearchInput
            disabled={disabled}
            value={value}
            onChange={setValue}
          />
        </div>
        <div className="rounded-lg border bg-white p-4">
          <SearchInput
            disabled={disabled}
            value={value}
            onChange={setValue}
            variant="light"
          />
        </div>
        <div className="rounded-lg border bg-gray-800 p-4">
          <SearchInput
            disabled={disabled}
            value={value}
            onChange={setValue}
            variant="dark"
          />
        </div>
      </div>
    </div>
  )

}
