import { Component } from "@choice-ui/switch"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      {Object.values(Size).map((size, index) => (
        <Fragment key={size}>
          <span>{size}</span>
          <div className="grid grid-cols-7 gap-2">
            <span></span>
            <span className="col-span-2">Disabled</span>
            <span className="col-span-2">Enabled</span>
            <span className="col-span-2">Focused</span>
          </div>
          <div className="grid grid-cols-7 gap-2">
            {Object.values(Variant).map((variant) => (
              <Fragment key={variant}>
                <span className="text-pink-500">{variant}</span>
                {Object.values(State).map((state) => (
                  <Fragment key={state}>
                    {[false, true].map((value) => (
                      <Switch
                        key={`${variant}-${state}-${value}`}
                        value={value}
                        onChange={() => {}}
                        variant={variant}
                        size={size}
                        disabled={state === State.Disabled}
                        focused={state === State.Focused}
                      />
                    ))}
                  </Fragment>
                ))}
              </Fragment>
            ))}
          </div>
          {index < Object.values(Size).length - 1 && <hr />}
        </Fragment>
      ))}
    </div>
  );
}

import { Component } from "@choice-ui/switch"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <Switch value={small} onChange={setSmall} size="small">
        Small size
      </Switch>
      <Switch value={medium} onChange={setMedium}>
        Medium size
      </Switch>
    </div>
  );
}

import { Component } from "@choice-ui/switch"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <Switch
        value={defaultVariant}
        onChange={setDefaultVariant}
        variant="default"
      >
        Default variant
      </Switch>
      <Switch
        value={accentVariant}
        onChange={setAccentVariant}
        variant="accent"
      >
        Accent variant
      </Switch>
      <Switch
        value={outlineVariant}
        onChange={setOutlineVariant}
        variant="outline"
      >
        Outline variant
      </Switch>
    </div>
  );
}

import { Component } from "@choice-ui/switch"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <Switch value={disabled} onChange={setDisabled} disabled>
        Disabled unchecked
      </Switch>
      <Switch value={disabledChecked} onChange={setDisabledChecked} disabled>
        Disabled checked
      </Switch>
    </div>
  );
}

import { Component } from "@choice-ui/switch"

export default function App() {
  return (
    <Switch
      value={value}
      onChange={setValue}
      label="Switch with legacy label prop"
    />
  );
}

import { Component } from "@choice-ui/switch"

export default function App() {
  return (
    <Tooltip content="This is a tooltip">
      <Switch value={tooltip} onChange={setTooltip}>
        Switch with tooltip
      </Switch>
    </Tooltip>
  );
}
