import { Component } from "@choice-ui/text-field"

export default function App() {
  return (
    <div className="flex flex-col gap-2">
      <TextField value={value} onChange={setValue} />
      <p className="text-secondary-foreground h-6">{value}</p>
    </div>
  );
}
