import { Component } from "@choice-ui/panel"

export default function App() {
  return (
    <AllotmentContainer header={<>Many Icon</>}>
      <Panel>
        <Panel.Title title="Panel row many icon">
          <IconButton tooltip={{ content: "Styles" }}>
            <Styles />
          </IconButton>
        </Panel.Title>

        <Panel.RowManyIcon
          isEditing={manyIconIsEditing.a}
          icons={[
            {
              id: "variable",
              element: (
                <IconButton tooltip={{ content: "Variable" }}>
                  <Visible />
                </IconButton>
              ),
            },
            {
              id: "variable-boolean",
              element: (
                <IconButton tooltip={{ content: "Variables Boolean" }}>
                  <VariablesBoolean />
                </IconButton>
              ),
            },
          ]}
        >
          <div
            className="bg-secondary-background focus-within:border-selected-boundary focus-within:bg-default-background flex h-6 min-w-0 flex-1 cursor-default items-center gap-1 rounded-md border border-transparent px-2"
            onClick={() =>
              setManyIconIsEditing({ ...manyIconIsEditing, a: true })
            }
          >
            <ColorTypeGradient />

            {manyIconIsEditing.a ? (
              <Input
                className="flex-1 pl-0"
                variant="reset"
                autoFocus
                value="Panel row many icon"
                onBlur={() =>
                  setManyIconIsEditing({ ...manyIconIsEditing, a: false })
                }
              />
            ) : (
              <span className="min-w-0 flex-1 truncate">
                Panel row many icon
              </span>
            )}
          </div>
        </Panel.RowManyIcon>

        <Panel.RowManyIcon
          isEditing={manyIconIsEditing.b}
          icons={[
            {
              id: "variable",
              element: (
                <IconButton tooltip={{ content: "Variable" }}>
                  <Visible />
                </IconButton>
              ),
            },
            {
              id: "variable-boolean",
              element: (
                <IconButton tooltip={{ content: "Variables Boolean" }}>
                  <VariablesBoolean />
                </IconButton>
              ),
            },
            {
              id: "variable-info",
              element: (
                <div className="flex h-6 w-6 items-center justify-center">
                  <InfoCircle />
                </div>
              ),
              alwaysShow: true,
            },
          ]}
        >
          <div
            className="bg-secondary-background focus-within:border-selected-boundary focus-within:bg-default-background flex h-6 min-w-0 flex-1 cursor-default items-center gap-1 rounded-md border border-transparent px-2"
            onClick={() =>
              setManyIconIsEditing({ ...manyIconIsEditing, b: true })
            }
          >
            <ColorTypeGradient />
            {manyIconIsEditing.b ? (
              <Input
                className="flex-1 pl-0"
                variant="reset"
                autoFocus
                value="Panel row many icon"
                onBlur={() =>
                  setManyIconIsEditing({ ...manyIconIsEditing, b: false })
                }
              />
            ) : (
              <span className="min-w-0 flex-1 truncate">
                Panel row many icon
              </span>
            )}
          </div>
        </Panel.RowManyIcon>
      </Panel>
    </AllotmentContainer>
  );
}

import { Component } from "@choice-ui/panel"

export default function App() {
  return (
    <AllotmentContainer
      header={
        <Switch
          size="small"
          label="Show labels"
          value={showLabels}
          onChange={(value) => setShowLabels(value)}
        />
      }
    >
      <Panel showLabels={showLabels}>
        <Panel.Title title="One Icon One Input">
          <IconButton tooltip={{ content: "Styles" }}>
            <Styles />
          </IconButton>
        </Panel.Title>

        <Panel.Row type="one-icon-one-input">
          <Panel.Label>Input</Panel.Label>
          <Input className="[grid-area:input]" placeholder="Input" />
          <IconButton
            className="[grid-area:icon]"
            tooltip={{ content: "Visible" }}
          >
            <Visible />
          </IconButton>
        </Panel.Row>
      </Panel>
    </AllotmentContainer>
  );
}
