import { Splitter } from "@choice-ui/splitter"

export default function App() {

  return (
    <Splitter className="h-64 w-128 rounded-xl border">
      <div className="grid h-full place-content-center">Pane 1</div>
      <div className="grid h-full place-content-center">Pane 2</div>
    </Splitter>
  )

}

import { Splitter } from "@choice-ui/splitter"

export default function App() {

  return (
    <Splitter className="h-64 w-128 rounded-xl border">
      <Splitter.Pane>
        <div className="grid h-full place-content-center">Pane 1</div>
      </Splitter.Pane>
      <Splitter.Pane>
        <div className="grid h-full place-content-center">Pane 2</div>
      </Splitter.Pane>
    </Splitter>
  )

}

import { Splitter } from "@choice-ui/splitter"
import { useState } from "react"

export default function App() {

  const [visible, setVisible] = useState(true)
  return (
    <div className="flex flex-col items-center justify-center gap-4">
      <Button
        onClick={() => {
          setVisible((visible) => !visible)
        }}
      >
        {visible ? "Hide Pane 2" : "Show Pane 2"}
      </Button>
      <div className="h-64 w-128 rounded-xl border">
        <Splitter
          snap
          onVisibleChange={(_index, value) => {
            setVisible(value)
          }}
        >
          <div className="grid h-full place-content-center">Pane 1</div>
          <Splitter.Pane visible={visible}>
            <div className="grid h-full place-content-center">Pane 2</div>
          </Splitter.Pane>
        </Splitter>
      </div>
    </div>
  )

}

import { Splitter } from "@choice-ui/splitter"
import { useRef } from "react"

export default function App() {

  const ref = useRef<SplitterHandle>(null)

  return (
    <div className="flex flex-col items-center justify-center gap-4">
      <Button
        onClick={() => {
          ref.current?.reset()
        }}
      >
        Reset
      </Button>
      <div className="h-64 w-128 rounded-xl border">
        <Splitter ref={ref}>
          <div className="grid h-full place-content-center">Pane 1</div>
          <div className="grid h-full place-content-center">Pane 2</div>
        </Splitter>
      </div>
    </div>
  )

}
