import { Pagination } from "@choice-ui/pagination"

export default function App() {
  return (
    <PaginationWrapper totalItems={100} />
  );
}

import { Pagination } from "@choice-ui/pagination"

export default function App() {
  return (
    <PaginationWrapper {...args} />
  );
}

import { Pagination } from "@choice-ui/pagination"

export default function App() {
  return (
    <div className="w-full space-y-4">
      <div className="rounded-lg border p-4">
        <h3 className="mb-2 text-lg font-semibold">Data Table Example</h3>
        <div className="mb-4 space-y-2">
          {currentItems.map((item) => (
            <div
              key={item}
              className="rounded bg-gray-50 px-3 py-2 dark:bg-gray-800"
            >
              {item}
            </div>
          ))}
        </div>
      </div>
      <Pagination
        currentPage={currentPage}
        totalItems={totalItems}
        itemsPerPage={itemsPerPage}
        onPageChange={setCurrentPage}
        onItemsPerPageChange={setItemsPerPage}
        pageSizeOptions={[10, 25, 50, 100]}
      >
        <Pagination.Spinner />
        <Pagination.Navigation />
        <Pagination.ItemsPerPage />
      </Pagination>
    </div>
  );
}

import { Pagination } from "@choice-ui/pagination"

export default function App() {
  return (
    <div className="w-full space-y-8">
      <div>
        <h3 className="mb-2 text-sm font-medium">
          Desktop View (All Features)
        </h3>
        <Pagination
          currentPage={currentPage}
          totalItems={500}
          itemsPerPage={itemsPerPage}
          onPageChange={setCurrentPage}
          onItemsPerPageChange={setItemsPerPage}
          showPageSizeSelector={true}
        >
          <Pagination.Spinner />
          <Pagination.Navigation />
          <Pagination.ItemsPerPage />
        </Pagination>
      </div>

      <div>
        <h3 className="mb-2 text-sm font-medium">Mobile View (Compact)</h3>
        <Pagination
          currentPage={currentPage}
          totalItems={500}
          itemsPerPage={itemsPerPage}
          onPageChange={setCurrentPage}
          onItemsPerPageChange={setItemsPerPage}
          maxPageButtons={5}
        >
          <Pagination.Navigation />
        </Pagination>
      </div>
    </div>
  );
}

import { Pagination } from "@choice-ui/pagination"

export default function App() {
  return (
    <div className="w-full space-y-4">
      <div className="rounded-lg border p-4">
        <div className="mb-4 flex items-center gap-4">
          <h3 className="text-lg font-semibold">Loading State Example</h3>
          <button
            type="button"
            onClick={() => setIsLoading(!isLoading)}
            className="rounded bg-blue-600 px-3 py-1 text-sm text-white hover:bg-blue-700"
          >
            Toggle Loading
          </button>
        </div>

        <div className="mb-4 space-y-2">
          {isLoading ? (
            <div className="space-y-2">
              <div className="h-8 animate-pulse rounded bg-gray-200 dark:bg-gray-700" />
              <div className="h-8 animate-pulse rounded bg-gray-200 dark:bg-gray-700" />
              <div className="h-8 animate-pulse rounded bg-gray-200 dark:bg-gray-700" />
            </div>
          ) : (
            Array.from({ length: itemsPerPage }, (_, i) => (
              <div
                key={`${currentPage}-${i}`}
                className="rounded bg-gray-50 px-3 py-2 dark:bg-gray-800"
              >
                Page {currentPage} - Item {i + 1}
              </div>
            ))
          )}
        </div>
      </div>

      <Pagination
        currentPage={currentPage}
        totalItems={250}
        itemsPerPage={itemsPerPage}
        onPageChange={handlePageChange}
        onItemsPerPageChange={handleItemsPerPageChange}
        loading={isLoading}
        pageSizeOptions={[10, 20, 50, 100]}
      >
        <Pagination.Spinner />
        <Pagination.Navigation />
        <Pagination.ItemsPerPage />
      </Pagination>
    </div>
  );
}
