import { Tabs } from "@choice-ui/tabs"

export default function App() {
  return (
    <Tabs
      value={selectedTab}
      onChange={(value) => {
        setSelectedTab(value)
      }}
    >
      <Tabs.Item value="tab1">Tab 1</Tabs.Item>
      <Tabs.Item value="tab2">Tab 2</Tabs.Item>
      <Tabs.Item value="tab3">Tab 3</Tabs.Item>
    </Tabs>
  );
}

