import { Tabs } from "@choice-ui/tabs"

export default function App() {
  return (
    <Tabs
      value={selectedTab}
      onChange={(value) => {
        setSelectedTab(value)
      }}
    >
      <Tabs.Item
        value="overview"
        disabled
      >
        Overview
      </Tabs.Item>
      <Tabs.Item
        value="settings"
        disabled
      >
        Settings
      </Tabs.Item>
      <Tabs.Item
        value="analytics"
        disabled
      >
        Analytics
      </Tabs.Item>
    </Tabs>
  );
}

import { Tabs } from "@choice-ui/tabs"

export default function App() {
  return (
    <div className="flex flex-col gap-2">
      <Checkbox
        value={disabled}
        onChange={(value) => setDisabled(value)}
      >
        Disabled
      </Checkbox>
      <div className="grid grid-cols-3 overflow-hidden rounded-xl border">
        <div className="bg-default-background p-8">
          <Tabs
            value={selectedTab}
            onChange={(value) => setSelectedTab(value)}
            disabled={disabled}
          >
            <Tabs.Item value="tab1">Tab 1</Tabs.Item>
            <Tabs.Item value="tab2">Tab 2</Tabs.Item>
            <Tabs.Item value="tab3">Tab 3</Tabs.Item>
          </Tabs>
        </div>
        <div className="bg-white p-8">
          <Tabs
            value={selectedTab}
            onChange={(value) => setSelectedTab(value)}
            variant="light"
            disabled={disabled}
          >
            <Tabs.Item value="tab1">Tab 1</Tabs.Item>
            <Tabs.Item value="tab2">Tab 2</Tabs.Item>
            <Tabs.Item value="tab3">Tab 3</Tabs.Item>
          </Tabs>
        </div>
        <div className="bg-gray-800 p-8">
          <Tabs
            value={selectedTab}
            onChange={(value) => setSelectedTab(value)}
            variant="dark"
            disabled={disabled}
          >
            <Tabs.Item value="tab1">Tab 1</Tabs.Item>
            <Tabs.Item value="tab2">Tab 2</Tabs.Item>
            <Tabs.Item value="tab3">Tab 3</Tabs.Item>
          </Tabs>
        </div>
      </div>
    </div>
  );
}
