import { AlertDialog } from "@choice-ui/alert-dialog"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <Button
        variant="secondary"
        onClick={handleClick}
      >
        Show Confirm Dialog
      </Button>
      {result !== null && (
        <p className="text-secondary-foreground text-body-small">
          Result: {result ? "Confirmed" : "Cancelled"}
        </p>
      )}
    </div>
  );
}

