import { Dialog } from "@choice-ui/dialog"

export default function App() {
  return (
    <>
      <Button onClick={() => setOpen(!open)}>Open Dialog</Button>
      <Dialog
        draggable
        overlay
        resizable={{ width: true, height: true }}
