import { Modal } from "@choice-ui/modal"
import { useState } from "react"

export default function App() {

  const [search, setSearch] = useState("")
  const [select, setSelect] = useState("option-1")
  const [input, setInput] = useState("")
  const [textarea, setTextarea] = useState("")
  const [multiLineInput, setMultiLineInput] = useState("")
  const [dropdown, setDropdown] = useState("option-1")
  const [open, setOpen] = useState(false)

  return (
    <Modal>
      <Modal.Header title="Modal" />
      <Modal.Content className="flex w-md flex-col gap-4 p-4">
        <div className="flex w-full items-center gap-2">
          <TextField
            className="flex-1 rounded-lg"
            size="large"
            placeholder="Please enter your name"
            selected={open}
            value={search}
            onChange={setSearch}
          >
            <TextField.Suffix className="px-1">
              <Select
                open={open}
                onOpenChange={setOpen}
                value={dropdown}
                onChange={setDropdown}
              >
                <Select.Trigger>
                  <Select.Value>{dropdown}</Select.Value>
                </Select.Trigger>
                <Select.Content>
                  {["option-1", "option-2", "option-3"].map((option) => (
                    <Select.Item
                      key={option}
                      value={option}
                    >
                      {option}
                    </Select.Item>
                  ))}
                </Select.Content>
              </Select>
            </TextField.Suffix>
          </TextField>
          <Button
            className="font-strong rounded-lg px-4"
            size="large"
          >
            Share
          </Button>
        </div>
        <Modal.Input
          size="large"
          label="Name"
          placeholder="Please enter your name"
          description="This is a description"
          value={input}
          onChange={setInput}
        />
        <Modal.Textarea
          label="Textarea"
          placeholder="Please enter your textarea"
          description="This is a description"
          value={textarea}
          onChange={setTextarea}
        />

        <Modal.Select
          label="Select"
          size="large"
          value={select}
          onChange={setSelect}
        >
          <Select.Trigger
            size="large"
            className="gap-2"
          >
            <Avatar
              photo={"https://api.dicebear.com/7.x/avataaars/svg?seed=Jennifer%20Miller"}
              name={faker.name.fullName()}
              size="small"
            />
            <span className="flex-1 truncate">{select || "Select"}</span>
          </Select.Trigger>
          <Select.Content>
            <Select.Item value="option-1">Option 1</Select.Item>
            <Select.Item value="option-2">Option 2</Select.Item>
            <Select.Item value="option-3">Option 3</Select.Item>
          </Select.Content>
        </Modal.Select>
      </Modal.Content>
    </Modal>
  )

}

