import { Component } from "@choice-ui/popover"

export default function App() {
  return (
    <>
      <div className="grid grid-cols-3 gap-2">
        {PLACEMENTS.map((option) => {
          return (
            <Button
              active={placement === option.value && open}
              variant="secondary"
              ref={(node) => {
                if (node) {
                  triggerRefs.current.set(option.value, node);
                }
              }}
              key={option.value}
              onMouseDown={() =>
                handleButtonClick(option.value as FloatingPlacement)
              }
            >
              {option.label}
            </Button>
          );
        })}
      </div>

      <Popover
        triggerRef={{ current: triggerRefs.current.get(placement) ?? null }}
        placement={placement}
        open={open}
        onOpenChange={setOpen}
      >
        <Popover.Content className="w-64 p-3">
          {faker.lorem.paragraph(3)}
        </Popover.Content>
      </Popover>
    </>
  );
}

