import { Component } from "@choice-ui/popover"

export default function App() {
  return (
    <div className="flex gap-4">
      <Popover
        open={open}
        onOpenChange={setOpen}
        autoUpdate
        draggable
        autoSize={autoSize}
      >
        <Popover.Trigger>
          <Button>Open</Button>
        </Popover.Trigger>
        <Popover.Header title="Auto Height" />
        <Popover.Content className="flex w-64 flex-col overflow-hidden">
          <ScrollArea className="flex flex-col">
            <ScrollArea.Viewport className="p-3">
              <ScrollArea.Content>
                {faker.lorem.sentences(50)}
              </ScrollArea.Content>
            </ScrollArea.Viewport>
          </ScrollArea>
        </Popover.Content>
      </Popover>

      <Button variant="secondary" onClick={() => setAutoSize(!autoSize)}>
        {autoSize ? "Disable Auto Size" : "Enable Auto Size"}
      </Button>
    </div>
  );
}

