import { Popover } from "@choice-ui/popover"
import { useState } from "react"

export default function App() {

  const [open, setOpen] = useState(false)
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")

  const handleSubmit = () => {
    console.log("Form submitted:", { name, email })
    setOpen(false)
    setName("")
    setEmail("")
  }

  const handleCancel = () => {
    setOpen(false)
    setName("")
    setEmail("")
  }

  const handleInputKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === "Escape") {
      // Blur the input
      e.currentTarget.blur()
      // Stop propagation to prevent Popover from closing
      e.stopPropagation()
    }
  }

  return (
    <Popover
      open={open}
      onOpenChange={setOpen}
    >
      <Popover.Trigger>
        <Button>Open Form Popover</Button>
      </Popover.Trigger>
      <Popover.Header title="User Information" />
      <Popover.Content className="w-80 p-4">
        <div className="flex flex-col gap-4">
          <div className="flex flex-col gap-2">
            <label
              htmlFor="popover-name-input"
              className="text-body-medium font-strong"
            >
              Name
            </label>
            <Input
              id="popover-name-input"
              placeholder="Enter your name"
              value={name}
              onChange={setName}
              onKeyDown={handleInputKeyDown}
              autoFocus
            />
          </div>
          <div className="flex flex-col gap-2">
            <label
              htmlFor="popover-email-input"
              className="text-body-medium font-strong"
            >
              Email
            </label>
            <Input
              id="popover-email-input"
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={setEmail}
              onKeyDown={handleInputKeyDown}
            />
          </div>
          <p className="text-secondary-foreground text-body-small">
            Press ESC in an input to blur it. Press ESC outside inputs to close the popover.
          </p>
        </div>
      </Popover.Content>
      <Popover.Footer className="flex justify-end gap-2 p-3">
        <Button
          variant="ghost"
          onClick={handleCancel}
        >
          Cancel
        </Button>
        <Button
          variant="primary"
          onClick={handleSubmit}
          disabled={!name || !email}
        >
          Submit
        </Button>
      </Popover.Footer>
    </Popover>
  )

}

import { Popover } from "@choice-ui/popover"
import { useState } from "react"

export default function App() {

  const [open, setOpen] = useState(false)
  const [result, setResult] = useState<string>("")

  const form = useForm({
    defaultValues: {
      name: "",
      email: "",
    },
    onSubmit: async ({ value }) => {
      setResult(JSON.stringify(value, null, 2))
      setOpen(false)
    },
  })

  const handleOpenChange = (isOpen: boolean) => {
    setOpen(isOpen)
    if (!isOpen) {
      form.reset()
    }
  }

  return (
    <div className="flex gap-8">
      <Popover
        as="form"
        open={open}
        onOpenChange={handleOpenChange}
        onSubmit={(e) => {
          e.preventDefault()
          e.stopPropagation()
          form.handleSubmit()
        }}
      >
        <Popover.Trigger>
          <Button>Add Contact</Button>
        </Popover.Trigger>
        <Popover.Header title="New Contact" />
        <Popover.Content className="w-80 space-y-4 p-4">
          <form.Field
            name="name"
            validators={{
              onChange: ({ value }) => {
                if ((value as string).length < 2) {
                  return "Name must be at least 2 characters"
                }
              },
            }}
          >
            {(field) => (
              <form.Input
                name={field.name}
                label="Name"
                value={field.state.value as string}
                onChange={field.handleChange}
                onBlur={field.handleBlur}
                placeholder="Enter name"
                error={field.state.meta.errors.join(", ")}
                autoFocus
              />
            )}
          </form.Field>

          <form.Field
            name="email"
            validators={{
              onBlur: ({ value }) => {
                const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
                if ((value as string) && !emailRegex.test(value as string)) {
                  return "Please enter a valid email"
                }
              },
            }}
          >
            {(field) => (
              <form.Input
                name={field.name}
                label="Email"
                type="email"
                value={field.state.value as string}
                onChange={field.handleChange}
                onBlur={field.handleBlur}
                placeholder="Enter email"
                error={field.state.meta.errors.join(", ")}
              />
            )}
          </form.Field>
        </Popover.Content>
        <Popover.Footer className="flex justify-end gap-2 p-3">
          <Button
            type="button"
            variant="ghost"
            onClick={() => setOpen(false)}
          >
            Cancel
          </Button>
          <form.Subscribe selector={(state) => [state.canSubmit, state.isSubmitting]}>
            {([canSubmit, isSubmitting]) => (
              <Button
                type="submit"
                variant="primary"
                disabled={!canSubmit}
                loading={isSubmitting as boolean}
              >
                Add
              </Button>
            )}
          </form.Subscribe>
        </Popover.Footer>
      </Popover>

      {result && (
        <div className="bg-secondary-background w-64 rounded-xl p-4">
          <strong className="mb-2 block">Result:</strong>
          <CodeBlock language="json">
            <CodeBlock.Content code={result} />
          </CodeBlock>
        </div>
      )}
    </div>
  )

}
