import { Component } from "@choice-ui/bells"

export default function App() {
  return (
    <div className="flex gap-2 capitalize">
      <Toaster />

      <Button
        variant="secondary"
        onClick={() => {
          bells({
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
          });
        }}
      >
        Render bell
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            toastOptions: {
              toasterId: "bells-toaster",
            },
            html: "Successfully <b class='text-red-500'>duplicated</b> project to <em class='text-blue-500'>My New Project</em>! üéâ",
            icon: <LayoutWallpaper />,
          });
        }}
      >
        Bell with HTML
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
          });
        }}
      >
        With icon
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
            onClose: (id) => {
              sonnerToast.dismiss(id);
            },
          });
        }}
      >
        With close button
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
            onClose: (id) => {
              sonnerToast.dismiss(id);
            },
            action: (id) => (
              <Chip
                size="medium"
                className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                onClick={() => {
                  sonnerToast.dismiss(id);
                }}
              >
                Action
              </Chip>
            ),
          });
        }}
      >
        With close button and action
      </Button>
      <Button
        variant="secondary"
        onClick={() => {
          bells({
            icon: <LayoutWallpaper />,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
            onClose: (id) => {
              sonnerToast.dismiss(id);
            },
            action: (id) => (
              <>
                <Chip
                  size="medium"
                  className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                  onClick={() => {
                    sonnerToast.dismiss(id);
                  }}
                >
                  Action
                </Chip>
                <Chip
                  size="medium"
                  className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                  onClick={() => {
                    sonnerToast.dismiss(id);
                  }}
                >
                  Dismiss
                </Chip>
              </>
            ),
            progress: true,
          });
        }}
      >
        With progress
      </Button>
    </div>
  );
}

import { Component } from "@choice-ui/bells"

export default function App() {
  return (
    <div className="flex flex-col gap-4">
      <Toaster id="bells-main" />
      <Toaster id="bells-secondary" offset={{ bottom: "128px" }} />

      <div className="flex flex-col gap-2">
        <h3 className="text-body-large-strong">Main Toaster</h3>
        <div className="flex gap-2">
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-main",
                text: "This bell appears in the main toaster at bottom-center.",
              });
            }}
          >
            Main Toaster
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-main",
                icon: <LayoutWallpaper />,
                text: "Bell with icon and close button",
                onClose: (id) => {
                  sonnerToast.dismiss(id);
                },
              });
            }}
          >
            With Close
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-main",
                variant: "success",
                icon: "‚úÖ",
                text: "Success bell in main toaster",
                progress: true,
              });
            }}
          >
            With Progress
          </Button>
        </div>
      </div>

      <div className="flex flex-col gap-2">
        <h3 className="text-body-large-strong">Secondary Toaster</h3>
        <div className="flex gap-2">
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-secondary",
                icon: <LayoutWallpaper />,
                text: "This bell appears in the secondary toaster at top-center.",
              });
            }}
          >
            Secondary Toaster
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-secondary",
                variant: "danger",
                icon: "‚ö†Ô∏è",
                html: "Danger bell with <strong>HTML</strong> content",
                onClose: (id) => {
                  sonnerToast.dismiss(id);
                },
              });
            }}
          >
            Danger Variant
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              bells({
                toasterId: "bells-secondary",
                variant: "accent",
                icon: <LayoutWallpaper />,
                text: "Bell with action button",
                action: (id) => (
                  <Chip
                    size="medium"
                    className="border-menu-boundary flex-none bg-transparent hover:bg-white/5"
                    onClick={() => {
                      sonnerToast.dismiss(id);
                    }}
                  >
                    Action
                  </Chip>
                ),
              });
            }}
          >
            With Action
          </Button>
        </div>
      </div>

      <div className="text-secondary-foreground text-body-small">
        <p className="font-strong">Usage:</p>
        <p>
          Multiple Toasters allow you to separate bells by context or
          priority. Each Toaster can have its own position, offset, and
          configuration. Use the <code>toasterId</code> prop to target a
          specific Toaster when calling <code>bells()</code>.
        </p>
      </div>
    </div>
  );
}

import { Component } from "@choice-ui/bells"

export default function App() {
  return (
    <div className="flex gap-2 capitalize">
      <Toaster />
      {colors.map((color) => (
        <Button
          key={color.color}
          variant="secondary"
          onClick={() => {
            bells({
              variant: color.color as
                | "default"
                | "accent"
                | "success"
                | "warning"
                | "danger"
                | "assistive",
              icon: <LayoutWallpaper />,
              text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
              onClose: (id) => {
                sonnerToast.dismiss(id);
              },
              action: (id) => (
                <Chip
                  size="medium"
                  className={tcx(
                    "flex-none",
                    color.color === "default"
                      ? "border-menu-boundary bg-transparent hover:bg-white/5"
                      : "border-black/10 bg-black/40 shadow-sm hover:bg-black/50"
                  )}
                  onClick={() => {
                    sonnerToast.dismiss(id);
                  }}
                >
                  {color.color}
                </Chip>
              ),
              progress: true,
            });
          }}
        >
          {color.color} color
        </Button>
      ))}
    </div>
  );
}
